<span class="line">        <span class="token class-name">String</span> token <span class="token operator">=</span> request<span class="token punctuation">.</span><span class="token function">getHeader</span><span class="token punctuation">(</span><span class="token class-name">Constants</span><span class="token punctuation">.</span><span class="token constant">HEAD_AUTHORIZATION</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
